    #[DataProvider('invalidInputProvider')]
    public function testInvalidInputs(string $input): void
    {
        $backdropBrightnessClass = BackdropBrightnessClass::parse($input);
        $this->assertNull($backdropBrightnessClass);
    }

    public static function invalidInputProvider(): array
    {
        return [
            ['backdrop-brightness-'],
            ['backdrop-brightness-25'],
            ['backdrop-brightness-300'],
            ['backdrop-brightness-medium'],
            ['not-a-backdrop-brightness-class'],
            // ['backdrop-brightness-[invalid]'],
            ['backdrop-brightness-[2'],
            ['backdrop-brightness-2.5'],
        ];
    }

    public function testEdgeCases(): void
    {
        // Test the smallest predefined backdrop brightness
        $smallestBrightness = BackdropBrightnessClass::parse('backdrop-brightness-0');
        $this->assertInstanceOf(BackdropBrightnessClass::class, $smallestBrightness);
        $this->assertSame('.backdrop-brightness-0{--tw-backdrop-brightness:brightness(0);-webkit-backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);}', $smallestBrightness->toCss());

        // Test the largest predefined backdrop brightness
        $largestBrightness = BackdropBrightnessClass::parse('backdrop-brightness-200');
        $this->assertInstanceOf(BackdropBrightnessClass::class, $largestBrightness);
        $this->assertSame('.backdrop-brightness-200{--tw-backdrop-brightness:brightness(2);-webkit-backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);}', $largestBrightness->toCss());

        // Test an arbitrary value with decimals
        $decimalArbitrary = BackdropBrightnessClass::parse('backdrop-brightness-[1.33]');
        $this->assertInstanceOf(BackdropBrightnessClass::class, $decimalArbitrary);
        $this->assertSame('.backdrop-brightness-\[1\.33\]{--tw-backdrop-brightness:brightness(1.33);-webkit-backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);}', $decimalArbitrary->toCss());

        // Test an arbitrary value with a different unit
        $differentUnitArbitrary = BackdropBrightnessClass::parse('backdrop-brightness-[120%]');
        $this->assertInstanceOf(BackdropBrightnessClass::class, $differentUnitArbitrary);
        $this->assertSame('.backdrop-brightness-\[120\%\]{--tw-backdrop-brightness:brightness(120%);-webkit-backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);backdrop-filter:var(--tw-backdrop-blur) var(--tw-backdrop-brightness) var(--tw-backdrop-contrast) var(--tw-backdrop-grayscale) var(--tw-backdrop-hue-rotate) var(--tw-backdrop-invert) var(--tw-backdrop-opacity) var(--tw-backdrop-saturate) var(--tw-backdrop-sepia);}', $differentUnitArbitrary->toCss());
    }
